INSERT INTO `block_custom` VALUES (1,'<?php\r\n$result = db_query(\"SELECT t.name Section, COUNT(v.field_value_value) Requirements, AVG(v.field_value_value) \'AvgScore\'\r\nFROM taxonomy_term_data t\r\nLEFT OUTER JOIN field_data_field_section s ON t.tid = s.field_section_tid\r\nLEFT OUTER JOIN field_data_field_tags tag ON s.entity_id = tag.entity_id\r\nLEFT OUTER JOIN field_data_field_value v ON tag.field_tags_tid = v.entity_id\r\nWHERE t.vid = 3\r\nGROUP BY t.name\r\nORDER BY t.name\");\r\n\r\n/*\r\necho \'<pre>\';\r\nforeach ($result as $row) {\r\nprint_r($row);\r\necho \'<hr />\';\r\n}\r\necho \'</pre>\';\r\n*/\r\n?>\r\n<table class=\"status-summary\">\r\n  <thead>\r\n    <tr>\r\n      <th scope=\"col\" class=\"section\">Section</th>\r\n      <th scope=\"col\" class=\"requirements\">Number of Requirements</th>\r\n      <th scope=\"col\" class=\"average\">Average of Compliance Rating Values</th>\r\n    </tr>\r\n  </thead>\r\n  <tbody>\r\n<?php\r\nforeach ($result as $row) {\r\n  echo \'<tr>\';\r\n    echo \'<td>\'.$row->Section.\'</td>\';\r\n    echo \'<td>\'.$row->Requirements.\'</td>\';\r\n    echo \'<td>\'.$row->AvgScore.\'</td>\';\r\n  echo \'</tr>\';\r\n}\r\n?>\r\n  </tbody>\r\n</table>','Custom SQL call to build summary table','php_code'),(2,'<p>If you have trouble accessing this page and need to request an alternate format, <a href=\"mailto:person@example.org\">please contact person@example.org.</a></p>','\"If you have trouble accessing this page...\"','plain_text'),(3,'<?php\r\n$result = db_query(\"SELECT p.name Section, COUNT(v.field_value_value) Requirements, AVG(v.field_value_value) \'AvgScore\'\r\nFROM taxonomy_term_data t\r\nLEFT OUTER JOIN taxonomy_term_hierarchy h ON t.tid = h.tid\r\nLEFT OUTER JOIN taxonomy_term_data p ON h.parent = p.tid\r\nLEFT OUTER JOIN field_data_field_section s ON t.tid = s.field_section_tid\r\nLEFT OUTER JOIN field_data_field_compliance_rating c ON s.entity_id = c.entity_id\r\nLEFT OUTER JOIN field_data_field_value v ON c.field_compliance_rating_target_id = v.entity_id\r\nWHERE t.vid = 3 AND p.tid IS NOT NULL\r\nGROUP BY p.name\r\nORDER BY t.name\");\r\n\r\n?>\r\n<table class=\"status-summary\">\r\n  <thead>\r\n    <tr>\r\n      <th scope=\"col\" class=\"section\">Section</th>\r\n      <th scope=\"col\" class=\"requirements\">Total Requirements *</th>\r\n      <th scope=\"col\" class=\"average\">Average Compliance Rating</th>\r\n    </tr>\r\n  </thead>\r\n  <tbody>\r\n<?php\r\nforeach ($result as $row) {\r\n  echo \'<tr>\';\r\n    echo \'<td class=\"section\">\'.$row->Section.\'</td>\';\r\n    echo \'<td>\'.$row->Requirements.\'</td>\';\r\n    echo \'<td>\'.$row->AvgScore.\'</td>\';\r\n  echo \'</tr>\';\r\n}\r\n?>\r\n  </tbody>\r\n</table>\r\n<p>* The total number of requirements include sub-requirements and sub-sub-requirements for which TRAC provides a basis for a compliance rating.</p>','(v2) Custom SQL call to build summary table','php_code');
